mapper.select:
/* 블로그 파일 가져오기 */ SELECT FILE_SEQ as 'seq', BLOG_SEQ as 'boardSeq', FILE_PATH as 'path', FILE_PATHNM as 'pathname', FILE_FILENM as 'filename', FILE_SIZE as 'size' FROM TB_PTFOLIO_BLOG_FILE
AND FILE_PATHNM = #{pathname}
AND FILE_SEQ = #{seq}

mapper.delete:
/* 블로그 파일 삭제 */
DELETE FROM TB_PTFOLIO_BLOG_FILE
WHERE FILE_SEQ = #{seq}
