mapper.select:
/* 블로그 페이징 */ SELECT X.* FROM (
SELECT A.*, B.IMG_SEQ, B.EDITOR_ID, B.IMG_PATHNM, B.IMG_FILENM, C.comtCnt FROM TB_CHANGOOS_BLOG A LEFT JOIN (SELECT * FROM TB_CHANGOOS_BLOG_IMAGE GROUP BY BOARD_SEQ) B ON BLOG_SEQ = B.BOARD_SEQ LEFT JOIN (SELECT BOARD_SEQ, COUNT(COMT_SEQ) as comtCnt FROM TB_CHANGOOS_BLOG_COMT GROUP BY BOARD_SEQ) C ON BLOG_SEQ = C.BOARD_SEQ
AND BLOG_TAG like '%${item}%'
AND BLOG_ENABLED = #{enabled}
SELECT A.*, B.IMG_SEQ, B.EDITOR_ID, B.IMG_PATHNM, B.IMG_FILENM, C.comtCnt FROM TB_CHANGOOS_BLOG A LEFT JOIN (SELECT * FROM TB_CHANGOOS_BLOG_IMAGE GROUP BY BOARD_SEQ) B ON BLOG_SEQ = B.BOARD_SEQ LEFT JOIN (SELECT BOARD_SEQ, COUNT(COMT_SEQ) as comtCnt FROM TB_CHANGOOS_BLOG_COMT GROUP BY BOARD_SEQ) C ON BLOG_SEQ = C.BOARD_SEQ
AND BLOG_ENABLED = #{enabled}
) X
ORDER BY BLOG_SEQ ${order} ORDER BY BLOG_TITLE ${order} ORDER BY BLOG_DATE ${order} ORDER BY BLOG_HITS ${order} ORDER BY comtCnt ${order} ORDER BY BLOG_DATE DESC, BLOG_SEQ DESC
LIMIT ${startRow}, ${perPgLine}

/* 블로그 개수 */
SELECT
	count(*)
FROM TB_CHANGOOS_BLOG


	AND BLOG_ENABLED = #{enabled}

mapper.update:
/* 블로그 수정하기 */ UPDATE TB_CHANGOOS_BLOG SET
BLOG_THUMB = null,
BLOG_THUMB = #{thumbnail},
BLOG_TITLE = #{title}, BLOG_CONTENTS = #{contents}, BLOG_TAG = #{tag}, BLOG_DATE = #{date}, BLOG_HITS = #{hits}, BLOG_ENABLED = #{enabled} WHERE BLOG_SEQ = #{seq}

mapper.delete:
/* 블로그 삭제하기 */
DELETE FROM TB_CHANGOOS_BLOG
WHERE BLOG_SEQ = #{seq}
