mapper.select:
/* 사진 댓글 수 가져오기 */
SELECT
	count(*)
FROM TB_CHANGOOS_PHOTO_COMT
WHERE PHOTO_SEQ = #{photoSeq}

mapper.insert:
/* 사진 댓글 추가하기 */
INSERT TB_CHANGOOS_PHOTO_COMT 
(PHOTO_SEQ, COMT_USERNAME, COMT_PWD, COMT_CONTENTS, COMT_DATE, PARENT_COMT)
VALUES (#{photoSeq}, #{username}, #{password}, #{contents}, #{date}, #{parentSeq});

mapper.delete:
/* 사진 댓글 삭제하기*/
DELETE FROM TB_CHANGOOS_PHOTO_COMT
WHERE COMT_SEQ = #{seq}

mapper.update:
/* 사진 댓글 수정하기 */
UPDATE TB_CHANGOOS_PHOTO_COMT
SET 
COMT_CONTENTS = #{contents}
WHERE COMT_SEQ = #{seq}
