mapper.select:
/* 사진 댓글 수 가져오기 */
SELECT
	count(*)
FROM TB_CHANGOOS_PHOTO_COMT
WHERE PHOTO_SEQ = #{photoSeq}

/* 사진 댓글 리스트 */
SELECT
	*
FROM TB_CHANGOOS_PHOTO_COMT
WHERE PHOTO_SEQ = #{photoSeq}
ORDER BY CONCAT(IF(PARENT_COMT is null, COALESCE(PARENT_COMT, ''), CONCAT('_', LPAD(PARENT_COMT,6,'0')) ), '_',LPAD(COMT_SEQ,6,'0'))

/* 사진 댓글 가져오기 */
SELECT
	*
FROM TB_CHANGOOS_PHOTO_COMT
WHERE COMT_SEQ = #{seq}

mapper.delete:
/* 사진 댓글 삭제하기*/
DELETE FROM TB_CHANGOOS_PHOTO_COMT
WHERE COMT_SEQ = #{seq}
