mapper.select:
SELECT PHOTO_SEQ as "seq", PHOTO_IMAGE as "image", PHOTO_SNAPSHT as "snapsht", PHOTO_NAME as "name", PHOTO_DESC as "desc", PHOTO_LOC as "location", PHOTO_DATE as "date", PHOTO_TIME as "time", PHOTO_TAG as "tag", PHOTO_DEVICE as "device", PHOTO_LIKECNT as "likeCnt" FROM TB_CHANGOOS_PHOTO
ORDER BY ${sort} ${order}
ORDER BY PHOTO_DATE DESC, PHOTO_TIME DESC, PHOTO_SEQ DESC

mapper.delete:
/* 사진 삭제하기*/
DELETE FROM TB_CHANGOOS_PHOTO
WHERE PHOTO_SEQ = #{seq}

mapper.insert:
/* 사진 추가하기*/
INSERT TB_CHANGOOS_PHOTO (PHOTO_IMAGE, PHOTO_SNAPSHT, PHOTO_NAME, PHOTO_DESC, PHOTO_LOC, PHOTO_DATE, PHOTO_TIME, PHOTO_TAG, PHOTO_DEVICE, PHOTO_LIKECNT)
VALUES (#{image}, #{snapsht}, #{name}, #{desc}, #{location}, #{date}, #{time}, #{tag}, #{device}, #{likeCnt})
