mapper.select:
/* 프로젝트 댓글 수 가져오기 */
SELECT
	count(*)
FROM TB_PTFOLIO_PROJECT_COMT
WHERE PCOMT_PROJ_SEQ = #{projectSeq}

mapper.insert:
/* 프로젝트 댓글 추가하기 */
INSERT TB_PTFOLIO_PROJECT_COMT 
(PCOMT_PROJ_SEQ, PCOMT_NAME, PCOMT_PWD, PCOMT_CONTENTS, PCOMT_DATE, PCOMT_PARENT_SEQ)
VALUES (#{projectSeq}, #{name}, #{password}, #{contents}, #{date}, #{parentSeq});

mapper.delete:
/* 프로젝트 댓글 삭제하기 */
DELETE FROM TB_PTFOLIO_PROJECT_COMT
WHERE PCOMT_SEQ = #{seq}

mapper.update:
/* 프로젝트 댓글 수정하기 */
UPDATE TB_PTFOLIO_PROJECT_COMT
SET 
PCOMT_CONTENTS = #{contents}
WHERE PCOMT_SEQ = #{seq}
