mapper.select:
/* 프로젝트 댓글 수 가져오기 */
SELECT
	count(*)
FROM TB_CHANGOOS_PROJECT_COMT
WHERE PROJ_SEQ = #{boardSeq}

mapper.insert:
/* 프로젝트 댓글 추가하기 */
INSERT TB_CHANGOOS_PROJECT_COMT 
(PROJ_SEQ, COMT_USERNAME, COMT_PWD, COMT_CONTENTS, COMT_DATE, PARENT_COMT)
VALUES (#{boardSeq}, #{username}, #{password}, #{contents}, #{date}, #{parentComt});

mapper.delete:
/* 프로젝트 댓글 삭제하기 */
DELETE FROM TB_CHANGOOS_PROJECT_COMT
WHERE COMT_SEQ = #{seq}

mapper.update:
/* 프로젝트 댓글 수정하기 */
UPDATE TB_CHANGOOS_PROJECT_COMT
SET 
COMT_CONTENTS = #{contents}
WHERE COMT_SEQ = #{seq}
