mapper.select:
SELECT FILE_SEQ as 'seq', FILE_PROJ_SEQ as 'boardSeq', FILE_REAL_NM as 'realNm', FILE_PATH_NM as 'pathNm', FILE_SIZE as 'size' FROM TB_PTFOLIO_PROJECT_FILES WHERE 1 = 1
AND FILE_PATH_NM = #{pathNm}
AND FILE_SEQ = #{seq}

mapper.delete:
DELETE FROM TB_PTFOLIO_PROJECT_FILES
WHERE 1 = 1 
AND FILE_SEQ = #{seq}
