mapper.select:
SELECT A.PROJ_SEQ as "seq", PROJ_TITLE as "title", PROJ_SUBTITLE as "subtitle", PROJ_DESC as "desc", PROJ_CONTENTS as "contents", PROJ_THUMB as "thumbnail", PROJ_SOURCECD as "sourcecode", PROJ_DEVELOPER as "developer", PROJ_HITS as "hits", PROJ_ENABLED as "enabled", count(B.COMT_SEQ) as 'comtCnt' FROM TB_CHANGOOS_PROJECT A LEFT OUTER JOIN TB_CHANGOOS_PROJECT_COMT B ON A.PROJ_SEQ = B.PROJ_SEQ
AND PROJ_ENABLED = #{enabled}
GROUP BY A.PROJ_SEQ
ORDER BY ${sort} ${order}
ORDER BY A.PROJ_SEQ DESC
