mapper.select:
SELECT PROJ_SEQ as "seq", PROJ_TITLE as "title", PROJ_SUBTITLE as "subtitle", PROJ_DESC as "desc", PROJ_SNAPSHT as "snapsht", PROJ_SOURCECD as "sourcecode", PROJ_DEVELOPER as "developer", PROJ_HITS as "hits", count(B.PCOMT_SEQ) as 'comtCnt' FROM TB_PTFOLIO_PROJECT A LEFT OUTER JOIN TB_PTFOLIO_PROJECT_COMT B ON A.PROJ_SEQ = B.PCOMT_PROJ_SEQ GROUP BY A.PROJ_SEQ
ORDER BY ${sort} ${order}
ORDER BY PROJ_SEQ DESC
