mapper.select:
/* 게시글 개수 */ SELECT count(*) FROM TB_CHANGOOS_STUDY
AND UPPER(STUDY_CATEGORY) = UPPER(#{category})
AND UPPER(STUDY_TITLE) LIKE CONCAT('%', UPPER(#{searchValue}), '%')

/* 이전 게시글 가져오기 */ SELECT A.* FROM TB_CHANGOOS_STUDY A
AND STUDY_ENABLED = true AND STUDY_SEQ < #{seq}
AND UPPER(STUDY_CATEGORY) = UPPER(#{category})
ORDER BY STUDY_SEQ DESC LIMIT 1

/* 다음 게시글 가져오기 */ SELECT A.* FROM TB_CHANGOOS_STUDY A
AND STUDY_ENABLED = true AND STUDY_SEQ > #{seq}
AND UPPER(STUDY_CATEGORY) = UPPER(#{category})
ORDER BY STUDY_SEQ ASC LIMIT 1

		/* 게시글 리스트 */
		SELECT
			A.*,
			B.comtCnt
		FROM 
			TB_CHANGOOS_STUDY A
			LEFT JOIN 
			(SELECT BOARD_SEQ, COUNT(COMT_SEQ) as 'comtCnt'
			FROM TB_CHANGOOS_STUDY_COMT
			GROUP BY BOARD_SEQ) B
			ON STUDY_SEQ = B.BOARD_SEQ
		 
			
	 		AND STUDY_ENABLED = #{enabled}
	  		
			
	 		AND UPPER(STUDY_CATEGORY) = UPPER(#{category})
	  		
	  		
	 		AND UPPER(STUDY_TITLE) LIKE CONCAT('%', UPPER(#{searchValue}), '%')
	  		
  		
  		
  		
		 	ORDER BY STUDY_SEQ ${order} 
		ORDER BY STUDY_CATEGORY ${order} 
		 	ORDER BY STUDY_TITLE ${order} 
		ORDER BY STUDY_CODE_LANG ${order} 
			ORDER BY STUDY_DATE ${order} 
			ORDER BY STUDY_HITS ${order} 
		 ORDER BY comtCnt ${order} 
		
		ORDER BY STUDY_DATE DESC, STUDY_SEQ DESC
		
		
		
		
 		LIMIT ${startRow}, ${perPgLine}
