mapper.select:
SELECT
	count(*)
FROM TB_PTFOLIO_COMTS
WHERE 1=1
AND COMT_BOARD_TYPE = #{boardType}
AND COMT_BOARD_SEQ = #{boardSeq}

mapper.insert:
INSERT TB_PTFOLIO_COMTS 
(COMT_BOARD_TYPE, COMT_BOARD_SEQ, COMT_NAME, COMT_PWD, COMT_CONTENTS, COMT_DATE, COMT_PARENT_SEQ)
VALUES (#{boardType}, #{boardSeq}, #{name}, #{password}, #{contents}, #{date}, #{parentSeq});

mapper.delete:
DELETE FROM TB_PTFOLIO_COMTS
WHERE 1 = 1
AND COMT_BOARD_TYPE = #{boardType}
AND COMT_BOARD_SEQ = #{boardSeq}

mapper.update:
UPDATE TB_PTFOLIO_COMTS
SET 
COMT_CONTENTS = #{contents}
WHERE COMT_SEQ = #{seq}
